package com.csi.summation;


public class Summation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int i, j;
		for(i=4;i>=1;i--){
			System.out.print("--------");
			for(j=1;j<=i;j++){
				
				System.out.print(" * "+" --- ");
			}System.out.println("--------");
		}
	}
}
