package com.csi.summation;

public class SwapWithVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10, y=20,z;
		System.out.println("\n Before Swap: "+" x = "+x+" y = "+y);
		z=x;
		x=y;
		y=z;
		System.out.println("\n After Swap: "+" x = "+x+" y = "+y);
		
	}

}
